mapper.insert:
INSERT INTO tradelens.product_image (
    product_id, image_url, is_main, sort, create_time
) VALUES (
             #{productId}, #{imageUrl}, #{isMain}, #{sort}, NOW()
         )

mapper.select:
SELECT p.id, p.title, p.price, p.product_status AS productStatus, (SELECT image_url FROM product_image WHERE product_id = p.id AND is_main = 1 LIMIT 1) AS mainImage, p.create_time AS createTime FROM product p WHERE p.user_id = #{userId} AND p.is_deleted = 0
AND p.product_status = #{status}
ORDER BY p.create_time DESC LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM product
WHERE user_id = #{userId}
AND is_deleted = 0

    AND product_status = #{status}

SELECT p.id, p.title, p.price, p.condition_level AS conditionLevel, (SELECT image_url FROM product_image WHERE product_id = p.id AND is_main = 1 LIMIT 1) AS mainImage,
u.nickname AS sellerNickname, u.avatar AS sellerAvatar, p.view_count AS viewCount, p.create_time AS createTime FROM product p INNER JOIN user u ON p.user_id = u.id WHERE p.is_deleted = 0 AND p.product_status = 2
AND p.title LIKE CONCAT('%', #{keyword}, '%')
AND p.category_id = #{categoryId}
AND p.price >= #{minPrice}
AND p.price <= #{maxPrice}
AND p.condition_level = #{condition}
ORDER BY
p.price ASC p.price DESC p.create_time DESC
LIMIT #{offset}, #{size}

SELECT COUNT(*)
FROM product p
WHERE p.is_deleted = 0
AND p.product_status = 2

    AND p.title LIKE CONCAT('%', #{keyword}, '%')


    AND p.category_id = #{categoryId}


    AND p.price >= #{minPrice}


    AND p.price <= #{maxPrice}


    AND p.condition_level = #{condition}
